import React, { useState } from "react";
import './ExpenseForm.scss';

const ExpenseForm = () => {
    const [enteredTitle, setEnteredTitle] = useState('');
    const [enteredAmount, setEnteredAmount] = useState('');
    const [enteredDate, setEnteredDate] = useState('');
    // const [userInput, setUserInput] = useState({
    //     enteredTitle: '',
    //     enteredAmount: '',
    //     enteredDate: '',
    // });

    const titleChangeHandler = (e) => {
        setEnteredTitle(e.target.value);
        // setUserInput((prevState) => {
        //     return { ...prevState, enteredTitle: e.target.value };
        // });
    };
    const amountChangeHandler = (e) => {
        setEnteredAmount(e.target.value);
        // setUserInput((prevState) => { return { ...prevState, enteredAmount: e.target.value } });
    };
    const dateChangeHandler = (e) => {
        setEnteredDate(e.target.value);
        // setUserInput((prevState) => { return { ...prevState, enteredDate: e.target.value } });
    };
    const newFormHandler = () => {

    }
    return (
        <form>
            <div className="new-expense__controls">
                <div className="new-expense__control">
                    <label>Title</label>
                    <input type="text" onChange={titleChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Amount</label>
                    <input type="number" min='0.01' step='0.01' onChange={amountChangeHandler} />
                </div>
                <div className="new-expense__control">
                    <label>Date</label>
                    <input type="date" min='2023-01-01' max='2025-12-31' onChange={dateChangeHandler} />
                </div>
            </div>
            <div className="new-expense__actions">
                <button type="submit" onClick={newFormHandler}>Add Expense</button>
            </div>
        </form>
    );
}
export default ExpenseForm;
