import React, { useState } from "react";
import ExpenseForm from "./ExpenseForm";
import "./NewExpense.scss";

const NewExpense = (props) => {
  const [expenseSwitch, SetExpenseSwitch] = useState(false);

  const saveNewExpenseHandler = (enteredExpenseData) => {
    const expenseData = {
      ...enteredExpenseData,
      id: Math.random().toString(),
    };
    props.onAddNewExpense(expenseData);
  };

  const addNewExpenseSwitchHandler = () => {
    return SetExpenseSwitch(true);
  };

  expenseSwitch === true ? (
    <div className="new-expense">
      <ExpenseForm saveExpenseData={saveNewExpenseHandler} />
    </div>
  ) : (
    <div className="new-expense">
      <button onClick={addNewExpenseSwitchHandler}>Add New Expense</button>
    </div>
  );
  //   return (
  //     <div className="new-expense">
  //       <ExpenseForm saveExpenseData={saveNewExpenseHandler} />
  //       <button onClick={addNewExpenseSwitchHandler}>Add New Expense</button>
  //     </div>
  //   );
};
export default NewExpense;
